def tonelli_shanks_large(a, p):
    # 步骤1：简化a到模p的范围内（处理大a的第一步）
    a = a % p
    if a == 0:
        return (0, 0)  # 0的平方还是0

    # 步骤2：用欧拉准则判断是否有解（核心！无论p是哪种类型都适用）
    # 原理：a^((p-1)/2) ≡ 1 mod p → 有解；≡p-1 → 无解
    legendre = pow(a, (p - 1) // 2, p)
    if legendre != 1:
        return None  # 无解

    # 步骤3：处理p≡3 mod4的情况（直接用公式，比通用算法更快）
    if p % 4 == 3:
        x = pow(a, (p + 1) // 4, p)
        return (x, p - x)

    # 步骤4：通用情况（p≡1 mod4），进入Tonelli-Shanks核心流程
    # 4.1 分解p-1 = Q * 2^S（把偶数因子剥离出来）
    Q = p - 1
    S = 0
    while Q % 2 == 0:
        Q //= 2
        S += 1

    # 4.2 找一个非二次剩余z（随便试，很快能找到）
    z = 2
    while pow(z, (p - 1) // 2, p) != p - 1:
        z += 1
    c = pow(z, Q, p)  # c是z^Q mod p，后面会用来迭代

    # 4.3 初始化变量
    x = pow(a, (Q + 1) // 2, p)  # 初始解的近似值
    t = pow(a, Q, p)  # 用来判断是否找到解的标志
    m = S  # 迭代次数上限

    # 4.4 迭代优化解
    while t != 1:
        # 找到最小的i（0<i<m）使得t^(2^i) ≡ 1 mod p
        temp, i = t, 0
        while temp != 1 and i < m:
            temp = pow(temp, 2, p)
            i += 1

        # 理论上不会出现i==m（前面欧拉准则已确认有解）
        if i == m:
            return None

        # 更新迭代变量（核心步骤，通过调整b来逼近解）
        b = pow(c, 1 << (m - i - 1), p)  # 1<<k 等价于2^k
        x = (x * b) % p
        t = (t * b * b) % p
        c = (b * b) % p
        m = i

    # 找到两个解：x和p-x
    return (x, p - x)

